

exports.ParseAddr = function(addr){
	var pos = addr.indexOf(":");
	if(pos == -1){
		return null;
	}else{
		var ip = addr.substring(0,pos);
		var port = Number(addr.substr(pos+1));
		return {
			ip:ip,
			port:port
		};
	}
}

exports.Megra = function(des,src){
	for(var key in src){
		des[key] = src[key];
	}	
}